const Sidebar = ({ sidebarOpen, setSidebarOpen }: SidebarProps) => {
  const [pageName, setPageName] = useLocalStorage("selectedMenu", "dashboard");
  const [userType, setUserType] = useState<string | null>(null);
  const [isTenant, setIsTenant] = useState(false);

  useEffect(() => {
    setIsTenant(checkIsTenant());
  }, []);
  // const { userType } = useAuth(); // Get user type from auth context
  useEffect(() => {
    const userInfo = localStorage.getItem("userInfo");
    if (userInfo) {
      const parsedUserInfo = JSON.parse(userInfo);
      setUserType(parsedUserInfo.userType);
    }
  }, []);
  const menuGroups =
    userType === "Super Admin"
      ? superAdminMenuGroups
      : userType === "Receptionist"
        ? receptionistMenuGroups
        : tenantMenuGroups;
  const logoSrc = "/images/logo4.webp";

  return (
    <ClickOutside onClick={() => setSidebarOpen(false)}>
      <div
        className={`fixed left-0 top-0 z-9999 flex h-screen w-50 flex-col overflow-y-hidden bg-black duration-300 ease-linear dark:bg-boxdark lg:translate-x-0 ${sidebarOpen ? "translate-x-0" : "-translate-x-full"
          }`}
      >
        <div className="mt-2 flex items-center justify-between gap-2 px-2">
          <Link className="inline-block" href="/">
            <Image
              className="dark:hidden"
              src={logoSrc}
              alt="Logo"
              width={300}
              height={60}
            />
          </Link>
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            aria-controls="sidebar"
            className="block lg:hidden"
          >
            <svg
              className="fill-current"
              width="20"
              height="18"
              viewBox="0 0 20 18"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M19 8.175H2.98748L9.36248 1.6875C9.69998 1.35 9.69998 0.825 9.36248 0.4875C9.02498 0.15 8.49998 0.15 8.16248 0.4875L0.399976 8.3625C0.0624756 8.7 0.0624756 9.225 0.399976 9.5625L8.16248 17.4375C8.31248 17.5875 8.53748 17.7 8.76248 17.7C8.98748 17.7 9.17498 17.625 9.36248 17.475C9.69998 17.1375 9.69998 16.6125 9.36248 16.275L3.02498 9.8625H19C19.45 9.8625 19.825 9.4875 19.825 9.0375C19.825 8.55 19.45 8.175 19 8.175Z"
                fill=""
              />
            </svg>
          </button>
        </div>
        {/* <!-- SIDEBAR HEADER --> */}
        <div className="no-scrollbar flex flex-col overflow-y-auto duration-300 ease-linear">
          <nav id="sidebarMenu" className="px-1">
            {menuGroups.map((group, groupIndex) => (
              <div id={`menuGroup-${groupIndex}`} key={groupIndex}>
                <ul
                  id={`menuGroup-${groupIndex}-items`}
                  className="mb-6 flex flex-col gap-1.5"
                >
                  {group.menuItems.map((menuItem, menuIndex) => (
                    <SidebarItem
                      id={`menuItem-${menuIndex}`}
                      key={menuIndex}
                      item={menuItem}
                      pageName={pageName}
                      setPageName={setPageName}
                    />
                  ))}
                </ul>
              </div>
            ))}
          </nav>
        </div>
      </div>
    </ClickOutside>
  );
};

export default Sidebar;
